public class Resta implements Operaciones {
	/**
	 * declaran variables necesarias en formato entero para realizar una resta
	 */
	@Override
	public int operaciones(int n1, int n2) {
		// TODO Auto-generated method stub
		return n1 - n2;
	}
	/**
	 * declaracion de variables vacias
	 */
	@Override
	public double operaciones(double n1, double n2) {
		// TODO Auto-generated method stub
		return 0;
	}

}
